CREATE TABLE IF NOT EXISTS study_inventory_t(
    StudyCode              VARCHAR(10)              NOT NULL     UNIQUE,
    ReviewerID             TINYINT      UNSIGNED    NOT NULL,
    SearchMethodID         TINYINT      UNSIGNED                 DEFAULT '1',
    DocumentTypeID         TINYINT      UNSIGNED,
    DatabaseID             TINYINT      UNSIGNED,
    YearPublished          SMALLINT     UNSIGNED,
    YearStudyBegan         SMALLINT     UNSIGNED,
    YearStudyEnd           SMALLINT     UNSIGNED,
    IsAnArticleDisabled    BOOLEAN                               DEFAULT '0',
    IsCountryExists        BOOLEAN                               DEFAULT '0',
    IsProfessionExists     BOOLEAN                               DEFAULT '0',
    IsInstitutionExists    BOOLEAN                               DEFAULT '0',
    AuthorsNames           VARCHAR(255),
    Title                  VARCHAR(350),
    ArticleUrl             VARCHAR(20),
    Hash                   VARCHAR(50),
    MonthPublished         VARCHAR(25)                           DEFAULT 'DummyValue',
    OtherInstitution       VARCHAR(500)                          DEFAULT 'DummyValue',
    Url                    VARCHAR(255)                          DEFAULT 'DummyValue',
    Doi                    VARCHAR(100)                           DEFAULT 'DummyValue',
    Pubmed                 VARCHAR(100)                           DEFAULT 'DummyValue',
    OriginalAbstract       VARCHAR(5000)                         DEFAULT 'DummyValue',
    RelevantPointOne       VARCHAR(500)                          DEFAULT 'DummyValue',
    RelevantPointTwo       VARCHAR(500)                          DEFAULT 'DummyValue',
    RelevantPointThree     VARCHAR(500)                          DEFAULT 'DummyValue',
    Citation               VARCHAR(500)                          DEFAULT 'DummyValue',

    PRIMARY KEY(StudyCode),
    FOREIGN KEY(ReviewerID)       REFERENCES  reviewer_t(ReviewerID),
    FOREIGN KEY(SearchMethodID)   REFERENCES  search_method_t(SearchMethodID),
    FOREIGN KEY(DocumentTypeID)   REFERENCES  document_type_t(DocumentTypeID),
    FOREIGN KEY(YearPublished)    REFERENCES  calendar_years_t(CalendarYear),
    FOREIGN KEY(DatabaseID)       REFERENCES  database_t(DatabaseID)
);

CREATE TABLE IF NOT EXISTS study_inventory_country_t(
   StudyCode           VARCHAR(10),
   CountryID           SMALLINT       UNSIGNED,

   PRIMARY KEY(StudyCode, CountryID),
   FOREIGN KEY(StudyCode)           REFERENCES study_inventory_t(StudyCode),
   FOREIGN KEY(CountryID)           REFERENCES country_t(CountryID)
);

CREATE TABLE IF NOT EXISTS study_inventory_profession_t(
   StudyCode           VARCHAR(10),
   ProfessionID        TINYINT       UNSIGNED,

   PRIMARY KEY(StudyCode, ProfessionID),
   FOREIGN KEY(StudyCode)           REFERENCES study_inventory_t(StudyCode),
   FOREIGN KEY(ProfessionID)        REFERENCES profession_t(ProfessionID)
);

CREATE TABLE IF NOT EXISTS study_inventory_institutions_t(
   StudyCode        VARCHAR(10),
   InstitutionID    SMALLINT       UNSIGNED,

   PRIMARY KEY(StudyCode, InstitutionID),
   FOREIGN KEY(StudyCode)     REFERENCES  study_inventory_t(StudyCode),
   FOREIGN KEY(InstitutionID) REFERENCES  institution_t(InstitutionID)
);

CREATE TABLE IF NOT EXISTS  article_process_t(
    ArticleProcessID   TINYINT       UNSIGNED   NOT NULL  AUTO_INCREMENT,
    Process            VARCHAR(50)                        UNIQUE,
    
    PRIMARY KEY(ArticleProcessID)
);

