CREATE TABLE IF NOT EXISTS  article_process_stage_t(
    StudyCode           VARCHAR(10),
    ReviewerID          TINYINT       UNSIGNED,
    ArticleProcessID    TINYINT       UNSIGNED,
    IsDisabled          BOOLEAN,

    PRIMARY KEY(StudyCode, ReviewerID),
    FOREIGN KEY(StudyCode)          REFERENCES  study_inventory_t(StudyCode),
